mapper.select:
select * from (select rownum rno, vno, r.email, r.gno, ino, goods_name, star, vcontent, review_simage, TO_CHAR(vdate, 'YYYY/MM/DD HH:MI:SS PM') vdate, nickname, member_spimage 
from h_review r, h_member m, h_goods g
where r.email = m.email and r.gno=g.gno and ino=#{ino} and review_simage IS NOT NULL) where rno between #{start} and #{end}

select * from (select rownum rno, vno, r.email, r.gno, ino, goods_name, star, vcontent, review_simage, TO_CHAR(vdate, 'YYYY/MM/DD HH:MI:SS PM') vdate, nickname, member_spimage 
from h_review r, h_member m, h_goods g
where r.email = m.email and r.gno=g.gno and ino=#{ino}
order by vdate desc) where rno between #{start} and #{end}

select * from (select rownum rno, vno, r.email, r.gno, ino, goods_name, star, vcontent, review_simage, TO_CHAR(vdate, 'YYYY/MM/DD HH:MI:SS PM') vdate, nickname, member_spimage 
from h_review r, h_member m, h_goods g
where r.email = m.email and r.gno=g.gno and ino=#{ino}
order by star desc) where rno between #{start} and #{end}

select * from (select * from
(select distinct rno, ino, company, ititle, category, tone, color, season, gno, goods_simage, goods_price, star_avg, star_count
from (select rownum rno, ino, company, ititle, category, tone, color, gno, season, goods_simage, goods_price, star_avg, star_count
	from(select ino, company, ititle, category, tone, color, gno, season, goods_simage, goods_price, ceil(avg(star)over(partition by ino)) as star_avg, 
		count(star)over(partition by ino) as star_count, row_number() over(partition by ititle order by goods_simage asc) as RowIdx
		from(select i.ino, company, ititle, interior_simage, category, tone, color, season, g.gno, goods_simage, goods_name, goods_price, vno, star
		from h_interior i, h_goods g, h_review v where i.ino(+) = g.ino and g.gno = v.gno(+)))
	where RowIdx='1' and  

	category=#{category}

	)

			where 

				tone like '%'||#{tone}||'%' 


	)

			where 

				color like '%'||#{color}||'%' 



				 season like '%'||#{season}||'%'  



)where rno between 

		#{startdo}

	 and

		#{enddo}

select count(*) from
(select distinct rno, ino, company, ititle, category, tone, color, season, gno, goods_simage, goods_price, star_avg, star_count
from (select rownum rno, ino, company, ititle, category, tone, color, gno, season, goods_simage, goods_price, star_avg, star_count
	from(select ino, company, ititle, category, tone, color, gno, season, goods_simage, goods_price, ceil(avg(star)over(partition by ino)) as star_avg, 
		count(star)over(partition by ino) as star_count, row_number() over(partition by ititle order by goods_simage asc) as RowIdx
		from(select i.ino, company, ititle, interior_simage, category, tone, color, season, g.gno, goods_simage, goods_name, goods_price, vno, star
		from h_interior i, h_goods g, h_review v where i.ino(+) = g.ino and g.gno = v.gno(+)))
	where RowIdx='1' and

	category=#{category}

	)

			where 

				tone like '%'||#{tone}||'%' 


	)

			where 

				color like '%'||#{color}||'%' 



				 season like '%'||#{season}||'%'

select ino, company, ititle, category, tone, color, gno, goods_simage, goods_price, star_avg, star_count
from(select ino, company, ititle, category, tone, color, gno, goods_simage, goods_price,  ceil(avg(star)over(partition by ino)) as star_avg, 
	count(star)over(partition by ino) as star_count, row_number() over(partition by ititle order by goods_simage asc) as RowIdx
	from(select i.ino, company, ititle, interior_simage, category, tone, color, g.gno, goods_simage, goods_name,  to_char(goods_price,'999,999,999,999') goods_price, vno, star
	from h_interior i, h_goods g, h_review v where i.ino(+) = g.ino and g.gno = v.gno(+)))
where RowIdx='1' and category=#{category}
